  return (
    <DayCard>
        <DayName>{data.day}</DayName>
        <Icon src={() => {
          if(data.text.split("").includes("Cloudy")) {
            return Cloudy;
          } else if(data.text.split("").includes("Sunny")) {
            return Sunny;
          } else if (data.text.split("").includes("Rainy")) {
            return Rainy;
          } else return;
        }
        }/>
        <Temperature> {Math.round(((data.high + data.low)  - 32 ) * 5/9) }<Unit>Â°C</Unit></Temperature>
    </DayCard>
  )
}
